public class Aula047CaracteristicasDosArrays {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * Características
		 * 
		 * Dimensão
		 * 
		 * ìndice  (começa em zero)
		 * 
		 * Elementos (itens que compõem o nosso array)
		 * 
		 * 
		 * tipo (igauis as variáveis simples)
		 * 
		 * 
		 * */
		
		//atribuindo valores aos índices(posições) de um array
		
		int[] lista = new int[5];
		lista[0] = 45;
		lista[1] = 10;
		lista[2] = 1;
		lista[3] = 1000;
		lista[4] = 45;
		
		
		int[] a = new int[10];
		
		a[0] = 0;
		a[1] = 111;
		a[2] = 222;
		a[3] = 333;
		a[4] = 444;
		a[5] = 1000;
		
		System.out.println(a[4]);
		System.out.println(a[5]);
		System.out.println(a[6]);
		
		
	}

}
